import React, { Component } from 'react';
import { Redirect,Link } from 'react-router-dom';
import axios from 'axios';

class CategoryDashBoard extends Component {

    state = {
        allproducts:[],
        isObject: false,
    }
    componentDidMount(){
        if(this.props.location.passlink){
            this.setState({ isObject: true });
            this.getproducts();
        }else{
            alert("Go back to home");
            //return <Redirect to='/' />
        }
    }
    getproducts= () =>{
        axios.get(this.props.location.passlink.link).then(response => this.setState({ allproducts:response.data }))
    }
    render() {
        if(this.state.isObject === false){
            return <Redirect to='/' />
        }
        return (
            <div>
                { this.state.isObject && <div><h1>{this.props.location.passlink.name}</h1>
                { this.state.allproducts.map(product =>(<div className="catagorybox"><Link to={{ pathname: '/productdetails', passlink:{ id:product.id,link:this.props.location.passlink.link,name:this.props.location.passlink.name } }} ><div className="categorydiv">
                            <p>{product.name}</p>
                            <img src={product.imagefield} />
                            <p>Available: {product.quantity}</p>
                            <p>Price: {product.price}</p>
                            <button className="button">Add to cart</button>
                            
                        </div></Link></div>))  }</div> }
            </div>
        )
    }
}

export default CategoryDashBoard
