import React, { Component } from 'react';
import {Redirect} from 'react-router-dom'
class Shoppingchart extends Component {
    state ={
        cartitems:[],
    }
    componentDidMount(){
        if(localStorage.getItem('cartitems')){
            //localStorage.removeItem('cartitems');
            this.setState({ cartitems: JSON.parse(localStorage.getItem('cartitems')) });
            // const cred = localStorage.getItem('cartitems');
            // this.setState({ cartitems: cred });
        }
    }
    addone(index) {
        const cartitems = this.state.cartitems;
      
        cartitems[index].count++;
        this.setState({ cartitems });
        localStorage.setItem("cartitems",JSON.stringify(this.state.cartitems));
      }
      deleteone(index) {
        const cartitems = this.state.cartitems;
      
        cartitems[index].count--;
        this.setState({ cartitems });
        localStorage.setItem("cartitems",JSON.stringify(this.state.cartitems));
      }
    removeItem(index) {
        const cartitems = this.state.cartitems;
      
        cartitems.splice(index, 1);
        this.setState({ cartitems });
        localStorage.setItem("cartitems",JSON.stringify(this.state.cartitems));
      }
    render() {
        return (
            <div>
            <div classNameName="shopping-cart">
            
            <div classNameName="title">
                Shopping Bag
            </div>
            {this.state.cartitems.length === 0 && <div><center><h1>Nothing in the Cart</h1></center></div>}
            {this.state.cartitems.map((item,i) =>(
                <div classNameName="item">
                <div classNameName="buttons">
                <span classNameName="delete-btn"></span>
                <span classNameName="like-btn"></span>
                </div>
            
                <div classNameName="image">
                <img src="item-3.png" alt="" />
                </div>
            
                <div classNameName="description">
                <span>Product ID: {item.id}</span>
                <span>Product Name: {item.name}</span>
                <span>Brown</span>
                </div>
            
                <div classNameName="quantity">
                <button onClick={() => this.deleteone(i) } classNameName="plus-btn" type="button" name="button">
                    -
                </button>
                <button classNameName="minus-btn" type="button" name="button">
                    {item.count}
                </button>
                <button onClick={() => this.addone(i) } classNameName="minus-btn" type="button" name="button">
                    +
                </button>
                </div>
            
                <div classNameName="total-price">{item.price * item.count}</div>
                <button onClick={() => this.removeItem(i) } type="button" name="button">
                    Remove
                </button>
            </div>
            ))}
            </div>
            <div className="wrapper fadeInDown">
                <div id="formContent">
                    
                    <h2 className="active">Customer Contact</h2>

                    
                    <form>
                    <input type="text" id="login" className="fadeIn second" name="customername"  placeholder="Your Name" />
                    <input type="text" id="password" className="fadeIn third" name="phnno"  placeholder="Phone Number" />
                    <input type="text" id="login" className="fadeIn second" name="email"  placeholder="email" />
                    <input type="text" id="password" className="fadeIn third" name="cityname"  placeholder="Name of City" />
                    <input type="text" id="password" className="fadeIn third" name="areaname"  placeholder="Name of Area" />
                    <textarea className="fadeIn third" placeholder="Address"></textarea>
                    <input type="submit" className="fadeIn fourth"  value="Register" />
                    </form>

                    
                    

                </div>
            </div>

        </div>
        );
    }
}

export default Shoppingchart;
